//! Azul DLL - C API bindings for the Azul GUI framework
//!
//! This crate provides the C-ABI functions for the Azul library.
//! The API is auto-generated from api.json using `azul-doc memtest dll`.
//!
//! To regenerate:
//!   cd doc && cargo run --release -- memtest dll
//!   cd doc && cargo run --release -- codegen rust

#![deny(improper_ctypes_definitions)]
#![allow(unused_imports)]
#![allow(dead_code)]
#![allow(unused_variables)]
#![allow(non_snake_case)]

#[macro_use]
extern crate alloc;
extern crate azul_core;
extern crate azul_css;
extern crate azul_layout;

#[cfg(not(target_arch = "wasm32"))]
pub mod desktop;

// Include the auto-generated API definitions
// Generated by: cd doc && cargo run --release -- memtest dll
#[cfg(feature = "c-api")]
mod __ffi_inner {
    include!(concat!(
        env!("CARGO_MANIFEST_DIR"),
        "/../target/memtest/dll_api.rs"
    ));
}

// Re-export the dll module at crate root level so that the rust API can use crate::dll::*
#[cfg(feature = "c-api")]
pub use __ffi_inner::__dll_api_inner::dll;

// Re-export the FFI functions (C-API)
#[cfg(feature = "c-api")]
pub mod ffi {
    pub use crate::__ffi_inner::__dll_api_inner::*;
}

// Include the auto-generated Rust API (public API without Az prefix)
// Generated by: cd doc && cargo run --release -- codegen rust
#[cfg(feature = "rust-api")]
include!(concat!(
    env!("CARGO_MANIFEST_DIR"),
    "/../target/codegen/azul.rs"
));

// Include the auto-generated Python bindings
// Generated by: cd doc && cargo run --release -- codegen python
#[cfg(feature = "python-extension")]
pub mod python {
    include!(concat!(
        env!("CARGO_MANIFEST_DIR"),
        "/../target/codegen/python_capi.rs"
    ));
}
