//! Azul DLL - C API bindings for the Azul GUI framework
//! 
//! This crate provides the C-ABI functions for the Azul library.
//! The API is auto-generated from api.json using `azul-doc memtest dll`.
//!
//! To regenerate:
//!   cd doc && cargo run --release -- memtest dll

#![deny(improper_ctypes_definitions)]
#![allow(unused_imports)]
#![allow(dead_code)]
#![allow(unused_variables)]
#![allow(non_snake_case)]

#[macro_use]
extern crate alloc;
extern crate azul_core;
extern crate azul_layout;
extern crate azul_css;

#[cfg(not(target_arch = "wasm32"))]
pub mod desktop;

// Include the auto-generated API definitions
// Generated by: cd doc && cargo run --release -- memtest dll
#[cfg(feature = "c-api")]
pub mod ffi {
    include!(concat!(env!("CARGO_MANIFEST_DIR"), "/../target/memtest/dll_api.rs"));
    pub use __dll_api_inner::*;
}
